// Knowledge Base for NexOperandi AI Customer Service
const message = $input.item.json.message.toLowerCase();
let relevantInfo = [];

const knowledgeBase = {
  pricing: {
    keywords: ['price', 'cost', 'pricing', 'plan', 'subscription', 'payment', 'how much'],
    info: 'NexOperandi Pricing: Starter Plan $500-2,000/month (small businesses 10-50 employees), Growth Plan $2,000-8,000/month (mid-market 50-500 employees), Enterprise Plan $8,000-50,000/month (500+ employees with custom features). All plans include 24/7 AI support, multi-channel integration, and 90-day ROI guarantee.'
  },
  features: {
    keywords: ['feature', 'capability', 'function', 'what can', 'how does', 'do you offer'],
    info: 'Our AI platform offers: 24/7 customer support automation, multi-channel integration (WhatsApp, Telegram, Slack, Email), sentiment analysis with smart escalation, 70% cost reduction, response time under 3 seconds, unlimited concurrent conversations, and support for 50+ languages.'
  },
  support: {
    keywords: ['help', 'support', 'issue', 'problem', 'not working', 'contact', 'reach'],
    info: 'For support: Email support@nexoperandi.ai (24/7 response), call 1-800-NEXOP-AI (business hours), or use our live chat. Premium support with 1-hour SLA available for Enterprise clients.'
  },
  billing: {
    keywords: ['invoice', 'payment', 'bill', 'charge', 'refund', 'billing cycle'],
    info: 'Billing: Monthly or annual subscriptions available (20% discount on annual). Access your billing dashboard at portal.nexoperandi.ai. For billing inquiries, email billing@nexoperandi.ai. We accept all major credit cards and wire transfers for Enterprise.'
  },
  integration: {
    keywords: ['integrate', 'connect', 'api', 'webhook', 'setup', 'installation'],
    info: 'Integration: Setup takes 15-30 minutes. We support REST API, webhooks, and native integrations with Slack, Teams, WhatsApp Business, Telegram, and 100+ other platforms via Zapier. Full API documentation at docs.nexoperandi.ai.'
  },
  performance: {
    keywords: ['performance', 'speed', 'uptime', 'reliability', 'sla'],
    info: 'Performance: 99.9% uptime SLA, <3 second response time, handles 50,000+ interactions/month per agent, automatic scaling, redundant infrastructure across multiple regions.'
  },
  roi: {
    keywords: ['roi', 'return', 'savings', 'value', 'worth it'],
    info: 'ROI: Average 3.5x return in 90 days, 70% cost reduction vs human agents ($0.02 per interaction vs $5-10), potential savings $249,000-499,000/month for high-volume operations. We offer a 90-day money-back guarantee.'
  },
  trial: {
    keywords: ['trial', 'demo', 'test', 'try', 'free'],
    info: 'Free Trial: 14-day free trial available with full features (up to 1,000 interactions). Book a personalized demo at nexoperandi.ai/demo. No credit card required for trial.'
  }
};

// Search for relevant information
for (const [category, data] of Object.entries(knowledgeBase)) {
  const hasKeyword = data.keywords.some(keyword => message.includes(keyword));
  if (hasKeyword) {
    relevantInfo.push(data.info);
  }
}

// If no specific match, provide general info
if (relevantInfo.length === 0) {
  relevantInfo.push('NexOperandi provides AI automation agents for businesses. We offer customer service automation, lead generation, and workflow automation with proven 3.5x ROI in 90 days.');
}

return {
  relevantInfo: relevantInfo,
  searchResults: relevantInfo.length,
  category: relevantInfo.length > 0 ? 'found' : 'general'
};

